import React, { useContext, useState } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
// import { Button } from "../../Button";
import axios from 'axios'
export function SignupForm() {

  const { switchToSignin } = useContext(AccountContext);
  const api = axios.create({
    baseURL:'http://localhost:8080/api/auth/signup'
  })
  // const [api,setApi] = useState('')


  const [username,setUsername] = useState('');
  const [email,setEmail] = useState('')
  const [password,setPassword] = useState('')

  function sendUserInput(){

    axios.post('http://localhost:8080/api/auth/signup', {
        headers: {
          'Content-Type': 'application/json',
        },
        body: {
          username:username,
          email:email,
          password:password,
        }
      })
      .then(function(response) {
        console.log(response)
        return response.json();
      });
  }
  function getApi(){
    api.then(function(response) {
      console.log(response)
      return response.json();
    });
  }
  const handleSubmit = (event) => {
    console.log("event is running",event)
    event.preventDefault()
    sendUserInput() 
  }

  return (
    <BoxContainer>
      <FormContainer onSubmit={handleSubmit}>
        <Input type="text" onChange={e => setUsername(e.target.value) }  placeholder="Full Name" />
        <Input type="email" onChange={e => setEmail(e.target.value)} placeholder="Email" />
        <Input type="password" onChange={e => setPassword(e.target.value) }placeholder="Password" />

      {/* <Marginer direction="vertical" margin={10} /> */}
      <SubmitButton type="submit"><BoldLink href="/login">
          Signup
        </BoldLink></SubmitButton>
      {/* <Marginer direction="vertical" margin="1em" /> */}
      <MutedLink href="#" onClick={switchToSignin}>
        Already have an account?

          Signup

      </MutedLink>
      </FormContainer>
    </BoxContainer>
  );
}
