import React, { useContext, useState } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
// import { Button } from "../../Button";

export function SignupForm() {

  const { switchToSignin } = useContext(AccountContext);

  const [username,setUsername] = useState('');
  const [email,setEmail] = useState('')
  const [password,setPassword] = useState('')

  function sendUserInput(){

    axios.post('http://localhost:8080/api/auth/signup', {
        // method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username:username,
          email:email,
          password:password,
        })
      })
      .then(function(response) {
        console.log(response)
        return response.json();
      });
  }
  const handleSubmit = (event) => {
    event.preventDefault()
    sendUserInput() 
  }

  return (
    <BoxContainer>
      <FormContainer onSubmit={handleSubmit}>
        <Input type="text" onChange={e => setUsername(e.target.value) }  placeholder="Full Name" />
        <Input type="email" onChange={e => setEmail(e.target.value)} placeholder="Email" />
        <Input type="password" onChange={e => setPassword(e.target.value) }placeholder="Password" />
       </FormContainer>
      <Marginer direction="vertical" margin={10} />
      <SubmitButton type="submit"><BoldLink href="/login" onClick={switchToSignin}>
          Signup
        </BoldLink></SubmitButton>
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#" onClick={switchToSignin}>
        Already have an account?

          Signup

      </MutedLink>
    </BoxContainer>
  );
}
