import React, { useContext, useState } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
// import { Button } from "../../Button";

export function SignupForm() {

  const { switchToSignin } = useContext(AccountContext);


  const [data ,setData] = useState({
    username:"",
    email:"",
    password:""
  })


const onChange = (e) =>{
  const newdata = {...data}
  newdata[e.target.id] = e.target.value
  setData(newdata)
}
 const onSubmit = (event) => {

    fetch('http://localhost:8080//api/auth/signup', {
        method: 'POST',
        body: JSON.stringify(setData)
      .then(function(response) {
        console.log(response)
        return response.json();
      })});

    event.preventDefault();
  }

  return (
    <BoxContainer>
      <FormContainer onSubmit={onSubmit()}>
        <Input type="text" id='username' value={data.username} onChange={e => onChange(e)} placeholder="Full Name" />
        <Input type="email" id='email' value={data.email} onChange={e => onChange(e)} placeholder="Email" />
        <Input type="password" id="password" value={data.password} onChange={e => onChange(e)} placeholder="Password" />
      </FormContainer>
      <Marginer direction="vertical" margin={10} />
      <SubmitButton type="submit">Signup</SubmitButton>
      {/* <Button>
        Sign
      </Button> */}
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#">
        Already have an account?
        <BoldLink href="#" onClick={switchToSignin}>
          Signin
        </BoldLink>
      </MutedLink>
    </BoxContainer>
  );
}
